import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Stats = () => {
  const [Year, setYear] = useState('');
  const [totals, setTotals] = useState(null);
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setTotals(null);

    try {
      setIsLoading(true);
      const response = await axios.get(`http://localhost:3000/api/football/stats/${Year}`);
      console.log('Fetched data:', response.data); // Debugging API response
      setTotals({ ...response.data }); // Ensure new reference
    } catch (error) {
      console.error('Error fetching data:', error); // Debugging
      setMessage(error.response?.data?.message || 'Error fetching data.');
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    console.log('Totals state updated:', totals); // Debugging state update
  }, [totals]);

  return (
    <div className="totals-container">
      <h2>Total Games Played, Draws, and Wins for a Given Year</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="Year">Year:</label>
          <input
            type="number"
            id="Year"
            name="Year"
            placeholder="Enter year"
            value={Year}
            onChange={(e) => setYear(e.target.value)}
            required
          />
        </div>
        <button type="submit">Get Totals</button>
      </form>

      {isLoading && <p>Loading...</p>}
      {message && <p className="message error">{message}</p>}
      {console.log('Rendering totals:', totals)} {/* Debugging render */}
      {totals && (
        <div className="results">
          <h3>Totals for {Year}</h3>
          <p><strong>Total Games Played:</strong> {totals?.GamesPlayed ?? 'N/A'}</p>
          <p><strong>Total Draws:</strong> {totals?.Draw ?? 'N/A'}</p>
          <p><strong>Total Wins:</strong> {totals?.Wins ?? 'N/A'}</p>
        </div>
      )}
    </div>
  );
};

export default Stats;
