// src/components/TeamStatsByName.js
import React, { useState } from 'react';
import axios from 'axios';

const TeamStatsByName = () => {
    const [teamName, setTeamName] = useState('');
    const [stats, setStats] = useState(null);
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setStats(null); // Reset stats on new submission

        try {
            const response = await axios.get(`http://localhost:5000/api/football/stats/${teamName}`);
            setStats(response.data);
        } catch (err) {
            setError('Team not found. Please enter a valid team name.');
        }
    };

    return (
        <div>
            <h2>Get Team Stats</h2>
            <form onSubmit={handleSubmit}>
                <label>Team Name:</label>
                <input
                    type="text"
                    value={teamName}
                    onChange={(e) => setTeamName(e.target.value)}
                    required
                />
                <button type="submit">Get Stats</button>
            </form>

            {error && <p style={{ color: 'red' }}>{error}</p>}

            {stats && (
                <div>
                    <h3>Stats for {teamName}</h3>
                    <p>Total Games Played: {stats.totalGamesPlayed}</p>
                    <p>Total Wins: {stats.totalWins}</p>
                    <p>Total Draws: {stats.totalDraws}</p>
                </div>
            )}
        </div>
    );
};

export default TeamStatsByName;
