import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
    const [Win, setWin] = useState('');
    const [totals, setTotals] = useState([]);
    const [message, setMessage] = useState('');
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('');
        setTotals([]);
        setLoading(true);

        // Validate Win input
        const winValue = parseInt(Win);
        if (isNaN(winValue)) {
            setMessage('Please enter a valid number for Win.');
            setLoading(false);
            return;
        }

        try {
            const response = await axios.get(`http://localhost:3000/api/football/teams/${winValue}`);
            console.log('Fetched Data:', response.data.data); // Debugging
            setTotals(response.data.data); // Adjust based on your API response
        } catch (error) {
            console.error('Error:', error.response); // Debugging
            setMessage(error.response?.data?.message || 'Error fetching data.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input
                    type="number"
                    value={Win}
                    onChange={(e) => setWin(e.target.value)}
                    placeholder="Enter Win value"
                />
                <button type="submit" disabled={loading}>
                    {loading ? 'Loading...' : 'Fetch Teams'}
                </button>
            </form>
            {message && <p>{message}</p>}
            <ul>
                {totals.map((team, index) => (
                    <li key={index}>{team.name}</li> // Adjust based on your data structure
                ))}
            </ul>
        </div>
    );
};

export default App;
