import React, { useState } from 'react';
import axios from 'axios';

const Stats = () => {
  const [Year, setYear] = useState('');
  const [teamStats, setTeamStats] = useState(null);
  const [message, setMessage] = useState('');

  const handle = (e) => {
    setYear(e.target.value);
  };

  const handleFetchStats = async (e) => {
    e.preventDefault();

    if (!Year) {
      setMessage({ text: 'Please enter a valid year.', type: 'error' });
      return;
    }

    try {
      const response = await axios.get(`http://localhost:3000/api/football/stats/${Year}`);
      setTeamStats(response.data); // Assuming the response contains team stats data
      setMessage({ text: 'Data fetched successfully!', type: 'success' });
    } catch (error) {
      setMessage({
        text: error.response?.data?.message || 'Error fetching data. Please try again.',
        type: 'error',
      });
      setTeamStats(null); // Clear stats on error
    }
  };

  return (
    <div>
      <form onSubmit={handleFetchStats}>
        <input
          type="Number"
          name="Year"
          placeholder="Enter Year"
          onChange={handleYearChange}
          value={Year}
          required
        />
        <button type="submit">Submit</button>
      </form>

      {message && (
        <p style={{ color: message.type === 'error' ? 'red' : 'green' }}>{message.text}</p>
      )}

      {teamStats && (
        <div>
          <h2>Team Statistics</h2>
          <p>Games Played: {teamStats.teamStatGamesPlayed}</p>
          <p>Wins: {teamStats.teamStatWin}</p>
          <p>Draws: {teamStats.teamstatDraw}</p>
          <p>Year: {teamStats.teamstatYear}</p>
        </div>
      )}
    </div>
  );
};

export default Stats;
