import React, { useState } from 'react';
import axios from 'axios';

const AddTeam = () => {
    const [teamData, setTeamData] = useState({
       
        "Team": '',
       "GamesPlayed" : '',
       "Win" : '',
        "Draw": '',
        "Loss": '',
        "GoalsFor": '',
        "GoalsAgainst": '',
        "Points": '',
       " Year": ''
    });

    const handleChange = (e) => {
        const bro = () => {
            
        }
        setTeamData({ ...teamData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        try {
            const response = await axios.post('http://localhost:3000/addteamdata', teamData);
            alert(response.data.message || 'Team data added successfully!');
            setTeamData({
             
            });
        } catch (error) {
            console.error('Error adding team data:', error);
            alert('Failed to add team data. Please try again.');
        }
    };

   

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                name="Team"
                placeholder="Team"
                onChange={handleChange}
                value={teamData.Team}
                required
            />
            <input
                type="number"
                name="GamesPlayed"
                placeholder="Games Played"
                onChange={handleChange}
                value={teamData.GamesPlayed}
                required
            />
            <input
                type="number"
                name="Win"
                placeholder="Wins"
                onChange={handleChange}
                value={teamData.Win}
                required
            />
            <input
                type="number"
                name="Draw"
                placeholder="Draws"
                onChange={handleChange}
                value={teamData.Draw}
                required
            />
            <input
                type="number"
                name="Loss"
                placeholder="Losses"
                onChange={handleChange}
                value={teamData.Loss}
            />
            <input
                type="number"
                name="GoalsFor"
                placeholder="Goals For"
                onChange={handleChange}
                value={teamData.GoalsFor}
            />
            <input
                type="number"
                name="GoalsAgainst"
                placeholder="Goals Against"
                onChange={handleChange}
                value={teamData.GoalsAgainst}
            />
            <input
                type="number"
                name="Points"
                placeholder="Points"
                onChange={handleChange}
                value={teamData.Points}
            />
            <input
                type="number"
                name="Year"
                placeholder="Year"
                onChange={handleChange}
                value={teamData.Year}
                required
            />
            <button type="submit">Submit</button>
        </form>
    );
};

export default AddTeam;
