import React, { useState } from 'react';
import axios from 'axios';

const Win = () => {
    const [Win, setWin] = useState('');
    const [totals, setTotals] = useState([]);
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('');
        setTotals([]);

        try {
            const response = await axios.get(`http://localhost:3000/api/football/teams/${Win}`);
            console.log('Fetched Data:', response.data.data); // Debugging
            setTotals(response.data.data); // Adjust based on your API response
        } catch (error) {
            console.error('Error:', error.response); // Debugging
            setMessage(error.response?.data?.message || 'Error fetching data.');
        }
    };

    return (
        <div className="totals-container">
            <h2>Total Games Played, Draws, and Wins for a Given Team</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="Win">Win:</label>
                    <input
                        type="number"
                        id="Win"
                        name="Win"
                        placeholder="Enter Win"
                        value={Win}
                        onChange={(e) => setWin(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Get Totals</button>
            </form>

            {message && <p className="message error">{message}</p>}

            {totals.length > 0 && (
                <div className="results">
                    <h3>Totals for {Win}</h3>
                    {totals.map((team, index) => (
                        <div key={index}>
                            <p><strong>Team:</strong> {team.Team ?? 'N/A'}</p>
                            <p><strong>Year:</strong> {team.Year ?? 'N/A'}</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Win;
