// src/components/AddTeam.js
import React, { useState } from 'react';
import axios from 'axios';

const AddTeam = () => {
    const [teamData, setTeamData] = useState({
       " Team": '',
       " GamesPlayed": '',
       " Win": '',
       " Draw": '',
        "Loss": '',
        "GoalsFor": '',
        "GoalsAgainst": '',
        "Points": '',
       " Year": ''
    });

    const handleChange = (e) => {
        setTeamData({ ...teamData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
         const sub  = await axios.post('http://localhost:3000/addteamdata', teamData);
           alert(sub.data.message)
               
            
        } catch (error) {
            console.error('Error adding team data:', error);
        }
    };

   
    return (
        <form onSubmit={handleSubmit}>
       
            <label for="team">Team:</label>
            <input type="Text" id="team" name="team"onChange={handleSubmit} value={teamData.Team} required/>
       
       
            <label for="gamesPlayed">Games Played:</label>
            <input type="number" id="gamesPlayed" name="gamesPlayed"onChange={handleSubmit} value={teamData.GamesPlayed} required/>
       
        
            <label for="win">Win:</label>
            <input type="number" id="win" name="win"onChange={handleSubmit} value={teamData.Win} required/>
       
        
        <label for="draw">Draw:</label>
        <input type="number" id="draw" name="draw" onChange={handleSubmit} value={teamData.Draw}required/>
    
   
        <label for="loss">Loss:</label>
        <input type="number" id="loss" name="loss" onChange={handleSubmit} value={teamData.Team}required/>
   
   
        <label for="goalsFor">Goals For:</label>
        <input type="number" id="goalsFor" name="goalsFor" required/>
   
   
        <label for="goalsAgainst">Goals Against:</label>
        <input type="number" id="goalsAgainst" name="goalsAgainst" required/>
    
    
        <label for="points">Points:</label>
        <input type="number" id="points" name="points" required/>
   
    
        <label for="year">Year:</label>
        <input type="number" id="year" name="year" required/>
    
    <button type="submit">Submit</button>
</form>
    );
};





export default AddTeam;
