import React, { useState } from 'react';
import axios from 'axios';

const deleteTeam = () => {
    const [teamData, setTeamData] = useState({
       
        Team: ''
    });

    const handleChange = (e) => {
        setTeamData({ ...teamData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!validateForm()) {
            alert('Please fill out all required fields correctly.');
            return;
        }
        try {
            const response = await axios.post('http://localhost:3000/api/football/delete/:team', teamData);
            alert(response.data.message || 'Team deleted successfully!');
            setTeamData({
                Team: '',
               
            });
        } catch (error) {
            console.error('Error deleting team data:', error);
            alert('Failed to delete team data. Please try again.');
        }
    };

    const validateForm = () => {
        const { Team, GamesPlayed, Win, Draw, Loss, GoalsFor, GoalsAgainst, Points, Year } = teamData;
        if (!Team || !GamesPlayed || !Win || !Draw || !Year) return false;
        if (GamesPlayed < 0 || Win < 0 || Draw < 0 || Loss < 0 || GoalsFor < 0 || GoalsAgainst < 0 || Points < 0 || Year < 0) return false;
        return true;
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                name="Team"
                placeholder="Team"
                onChange={handleChange}
                value={teamData.Team}
                required
            />
            <input
                type="number"
                name="GamesPlayed"
                placeholder="Games Played"
                onChange={handleChange}
                value={teamData.GamesPlayed}
                required
            />
            <input
                type="number"
                name="Win"
                placeholder="Wins"
                onChange={handleChange}
                value={teamData.Win}
                required
            />
            <input
                type="number"
                name="Draw"
                placeholder="Draws"
                onChange={handleChange}
                value={teamData.Draw}
                required
            />
            <input
                type="number"
                name="Loss"
                placeholder="Losses"
                onChange={handleChange}
                value={teamData.Loss}
            />
            <input
                type="number"
                name="GoalsFor"
                placeholder="Goals For"
                onChange={handleChange}
                value={teamData.GoalsFor}
            />
            <input
                type="number"
                name="GoalsAgainst"
                placeholder="Goals Against"
                onChange={handleChange}
                value={teamData.GoalsAgainst}
            />
            <input
                type="number"
                name="Points"
                placeholder="Points"
                onChange={handleChange}
                value={teamData.Points}
            />
            <input
                type="number"
                name="Year"
                placeholder="Year"
                onChange={handleChange}
                value={teamData.Year}
                required
            />
            <button type="submit">Submit</button>
        </form>
    );
};

export default deleteTeam;
