import React, { useState } from 'react';
import axios from 'axios';
const stats = () => {
    const [teamName, setTeamName] = useState('');
    const [message, setMessage] = useState('');
   
    const handleChange = (e) => {
      setTeamName(e.target.value);
    };
   
    const handleDelete = async (e) => {
      e.preventDefault();
   
      if (!teamName) {
        setMessage({ text: 'Please enter a team name.', type: 'error' });
        return;
      }
   
      try {
        const response = await axios.get(`http://localhost:3000/api/football/stats/${Year}`);
        setMessage({ text: response.data.message, type: 'success' });
        setTeamName(''); // Clear the input field
      } catch (error) {
        setMessage({
          text: error.response?.data?.message || 'Error deleting team. Please try again.',
          type: 'error',
        });
      }
    };
   

    return (
        <div>
            <form onSubmit={handleDelete}>
               
                <input
                    type="number"
                    name="Year"
                    placeholder="Year"
                    onChange={handleChange}
                    value={teamName}
                    required
                />
                <button type="submit">Submit</button>
            </form>
)
            {showData && (
                <div>
                    <h2>Data</h2>
                   
                    <p>Games Played: {teamName.GamesPlayed}</p>
                    <p>Wins: {teamName.Win}</p>
                    <p>Draws: {teamName.Draw}</p>
                    <p>Year: {teamName.Year}</p>
                </div>
            )}
        </div>
    );
};

export default stats;
