import React, { useState } from 'react';
import axios from 'axios';

const DeleteTeam = () => {
    const [teamData, setTeamData] = useState('' );
    const [message,setMessage]=usbe

    const handleChange = (e) => {
        const { name, value } = e.target;
        setTeamData((prevState) => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault(); // Prevent default form submission behavior

        try {
            // Sending a delete request to the backend
            const response = await axios.delete(`http://localhost:3000/delete/${teamData}`); 
                 // Assuming your backend expects this format
                setMessage({text:response.data.message,type:'sucess'})
            alert(response.data.message || 'Team data deleted successfully!');
            setTeamData( '' ); // Reset the form
        } catch (error) {
            console.error('Error deleting team data:', error);
            alert('Failed to delete team data. Please try again.');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                name="Team"
                placeholder="Team"
                onChange={handleChange}
                value={teamData.Team}
                required
            />
            <button type="submit">Delete Team</button>
        </form>
    );
};

export default DeleteTeam;
