import React, { useState } from 'react';
import axios from 'axios';

const DeleteTeam = () => {
    const [teamData, setTeamData] = useState({ Team: '' });

    const handleChange = (e) => {
        const { Team, value } = e.target;
        setTeamData((prevState) => ({
            ...prevState,
            [Team]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault(); // Prevent default form submission behavior

        try {
            // Sending a delete request to the backend
            const response = await axios.delete('http://localhost:3000/delete/:Team', {
                data: { Team: teamData.Team }, // Assuming your backend expects this format
            });
            alert(response.data.message || 'Team data deleted successfully!');
            setTeamData({ Team: '' }); // Reset the form
        } catch (error) {
            console.error('Error deleting team data:', error);
            alert('Failed to delete team data. Please try again.');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                name="Team"
                placeholder="Team"
                onChange={handleChange}
                value={teamData.Team}
                required
            />
            <button type="submit">Delete Team</button>
        </form>
    );
};

export default DeleteTeam;
