// src/components/UpdateTeam.js
import React, { useState } from 'react';
import axios from 'axios';

const UpdateTeam = () => {
    const [teamName, setTeamName] = useState('');
    const [teamData, setTeamData] = useState({
        gamesPlayed: '',
        win: '',
        draw: '',
        loss: '',
        goalsFor: '',
        goalsAgainst: '',
        points: '',
        year: ''
    });

    const handleChange = (e) => {
        setTeamData({ ...teamData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        await axios.post(`http://localhost:5000/api/football/update/${teamName}`, teamData);
        alert('Team updated successfully!');
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Team Name:</label>
                <input type="text" value={teamName} onChange={(e) => setTeamName(e.target.value)} required />
            </div>
            {Object.keys(teamData).map((key) => (
                <div key={key}>
                    <label>{key.charAt(0).toUpperCase() + key.slice(1)}:</label>
                    <input type="text" name={key} value={teamData[key]} onChange={handleChange} required />
                </div>
            ))}
            <button type="submit">Update Team</button>
        </form>
    );
};

export default UpdateTeam;
