// src/components/AddTeam.js
import React, { useState } from 'react';
import axios from 'axios';

const AddTeam = () => {
    const [teamData, setTeamData] = useState({
       " Team": '',
       " GamesPlayed": '',
       " Win": '',
       " Draw": '',
        "Loss": '',
        "GoalsFor": '',
        "GoalsAgainst": '',
        "Points": '',
       " Year": ''
    });

    const handleChange = (e) => {
    const {name,value}=e.target
        setTeamData({ ...teamData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
         const sub  = await axios.post('http://localhost:3000/addteamdata', teamData);
           alert(sub.data.message)
               
            
        } catch (error) {
            console.error('Error adding team data:', error);
        }
    };

   
    return (
        <form onSubmit={handleSubmit}>
       
            
            <input type="Text"  name="Team"onChange={handleSubmit} value={teamData.Team} required/>
       
       
            
            <input type="Number"  name="GamesPlayed"onChange={handleSubmit} value={teamData.GamesPlayed} required/>
       
        
            <input type="Number"  name="Win"onChange={handleSubmit} value={teamData.Win} required/>
       
        
        <label for="draw">Draw:</label>
        <input type="Number" name="Draw" onChange={handleSubmit} value={teamData.Draw}required/>
    
   
        <label for="loss">Loss:</label>
        <input type="Number"  name="Loss" onChange={handleSubmit} value={teamData.Loss}/>
   
   
        <label for="goalsFor">Goals For:</label>
        <input type="Number"  name="GoalsFor"onChange={handleSubmit} value={teamData.GoalsFor} />
   
   
        <label for="goalsAgainst">Goals Against:</label>
        <input type="Number"  name="GoalsAgainst"onChange={handleSubmit} value={teamData.GoalsAgainst}/>
    
    
        <label for="points">Points:</label>
        <input type="Number"  name="Points"onChange={handleSubmit} value={teamData.Points} />
   
    
        <label for="year">Year:</label>
        <input type="Number" name="Year" onChange={handleSubmit} value={teamData.Year}required/>
    
    <button type="submit">Submit</button>
</form>
    );
};





export default AddTeam;
