import React, { useState } from 'react';
import axios from 'axios';

const AddTeam = () => {
    const [teamData, setteamData] = useState({
        Team: '',
        GamesPlayed: '',
        Win: '',
        Draw: '',
        Loss: '',
        GoalsFor: '',
        GoalsAgainst: '',
        Points: '',
        Year: ''
    });
   



    const handleChange = (e) => {
        const { name, value } = e.target;
    
        // Convert value to a number if the field should be numeric
        const numericFields = ['GamesPlayed', 'Win', 'Draw', 'Loss', 'GoalsFor', 'GoalsAgainst', 'Points', 'Year'];
        setteamData({
            ...teamData,
            [name]: numericFields.includes(name) ? Number(value) : value, // Convert to number for numeric fields
        });
    };
    

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:3000/addteamdata', teamData);
            alert(response.data.message || 'Team data added successfully!');
            setteamData({
                Team: '',
                GamesPlayed: '',
                Win: '',
                Draw: '',
                Loss: '',
                GoalsFor: '',
                GoalsAgainst: '',
                Points: '',
                Year: '',
            });
        } catch (error) {
            console.error('Error adding team data:', error);
            alert('Failed to add team data. Please try again.');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                name="Team"
                placeholder="Team"
                onChange={handleChange}
                value={teamData.Team}
                required
            />
            <input
                type="number"
                name="GamesPlayed"
                placeholder="Games Played"
                onChange={handleChange}
                value={teamData.GamesPlayed}
                required
            />
            <input
                type="number"
                name="Win"
                placeholder="Wins"
                onChange={handleChange}
                value={teamData.Win}
                required
            />
            <input
                type="number"
                name="Draw"
                placeholder="Draws"
                onChange={handleChange}
                value={teamData.Draw}
                required
            />
            <input
                type="number"
                name="Loss"
                placeholder="Losses"
                onChange={handleChange}
                value={teamData.Loss}
            />
            <input
                type="number"
                name="GoalsFor"
                placeholder="Goals For"
                onChange={handleChange}
                value={teamData.GoalsFor}
            />
            <input
                type="number"
                name="GoalsAgainst"
                placeholder="Goals Against"
                onChange={handleChange}
                value={teamData.GoalsAgainst}
            />
            <input
                type="number"
                name="Points"
                placeholder="Points"
                onChange={handleChange}
                value={teamData.Points}
            />
            <input
                type="number"
                name="Year"
                placeholder="Year"
                onChange={handleChange}
                value={teamData.Year}
                required
            />
            <button type="submit">Submit</button>
        </form>
    );
};

export default AddTeam;
