import React, { useState } from 'react';
import axios from 'axios';

const Stats = () => {
    const [Win, setWin] = useState('');
    const [totals, setTotals] = useState(null);
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('');
        setTotals(null);

        try {
            const response = await axios.get('http://localhost:3000/api/football/teams/:Win', {
                params: { Win },
            });
            // Access the stats from the response
            setTotals(response.data.Win);
        } catch (error) {
            setMessage(error.response?.data?.message || 'Error fetching data.');
        }
    };

    return (
        <div className="totals-container">
            <h2>Total  Wins more than</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="Win">Win:</label>
                    <input
                        type="number"
                        id="Win"
                        name="Win"
                        placeholder="Enter Win"
                        value={Win}
                        onChange={(e) => setWin(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Get teams</button>
            </form>

            {message && <p className="message error">{message}</p>}

            {totals && (
                <div className="results">
                    <h3>Totals for {Win}</h3>
                    <p><strong>Team</strong> {totals.GamesPlayed}</p>
                    <p><strong> Draws:</strong> {totals.Draw}</p>
                    <p><strong>Wins:</strong> {totals.Win}</p>
                    <p><strong> Games Played:</strong> {totals.GamesPlayed}</p>
                    <p><strong>loss</strong> {totals.Draw}</p>
                    <p><strong>Total Wins:</strong> {totals.Win}</p>
                </div>
            )}
        </div>
    );
};

export default Stats;
