import React, { useState } from 'react';
import axios from 'axios';

const UpdateTeam = () => {
    const [teamName, setTeamName] = useState('');
    const [teamData, setTeamData] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const handleTeamNameSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');
        try {
            const response = await axios.get(`http://localhost:3000/api/football/team/${teamName}`);
            setTeamData(response.data);
        } catch (err) {
            console.error('Error fetching team data:', err);
            setError('Failed to fetch team data. Please check the team name and try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleChange = (e) => {
        setTeamData({ ...teamData, [e.target.name]: e.target.value });
    };

    const handleUpdateSubmit = async (e) => {
        e.preventDefault();
        setError('');
        try {
            const response = await axios.post(`http://localhost:3000/api/football/update/${teamName}`, teamData);
            alert(response.data.message || 'Team data updated successfully!');
        } catch (err) {
            console.error('Error updating team data:', err);
            setError('Failed to update team data. Please try again.');
        }
    };

    return (
        <div>
            {!teamData ? (
                <form onSubmit={handleTeamNameSubmit}>
                    <input
                        type="text"
                        placeholder="Enter Team Name"
                        value={teamName}
                        onChange={(e) => setTeamName(e.target.value)}
                        required
                    />
                    <button type="submit" disabled={loading}>
                        {loading ? 'Loading...' : 'Fetch Team Data'}
                    </button>
                </form>
            ) : (
                <form onSubmit={handleUpdateSubmit}>
                    <h3>Update Team Data for {teamName}</h3>
                    {Object.keys(teamData).map((key) => (
                        <div key={key}>
                            <label>
                                {key}:
                                <input
                                    type="text"
                                    name={key}
                                    value={teamData[key] || ''}
                                    onChange={handleChange}
                                />
                            </label>
                        </div>
                    ))}
                    <button type="submit">Update Data</button>
                </form>
            )}
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
};

export default UpdateTeam;
